mapper.select:
SELECT u.*, c.company_name FROM sys_user u LEFT JOIN company_info c ON u.company_id = c.company_id WHERE u.delete_flag = 0
AND u.username LIKE CONCAT('%', #{query.username}, '%')
AND u.user_type = #{query.userType}
AND u.status = #{query.status}
AND u.company_id = #{query.companyId}
ORDER BY u.create_time DESC

mapper.update:
UPDATE sys_user
SET delete_flag = 1, update_time = NOW()
WHERE user_id = #{userId}
